<template>
  <div>
    <img ref="img" :src="logo" :width="logoWidth" @dblclick="uploadImage">
    <input type="file" ref="fileInput" hidden @change="fileInputChange">
  </div>
</template>

<script>
export default {
  data() {
    return {
      logo: 'data:image/svg+xml;base64,',
      logoWidth: '180'
    }
  },
  methods: {
    uploadImage() {
      this.$refs.fileInput.click()
    },
    fileInputChange(event) {
      const reader = new FileReader()
      reader.onload = event => this.$refs.img.src = event.target.result
      reader.readAsDataURL(event.target.files[0])
    }
  }
}
</script>
